package PreDefined_PackeageEX;

import java.util.*;

public class PreDefEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scan =new Scanner(System.in);
		System.out.println("the old salary is :");
		int old=scan.nextInt();
		System.out.println("the Hike salary is :");
		int Hike=scan.nextInt();
		int New=old +Hike;
		System.out.println("the New salary is : "+New);
		

	}

}
